mapper.select:
select id, name
from places
where area_code = #{areaCode}

select p.id, p.phone, p.name, p.address, p.area_code, pr.rate_id, pr.price, ps.service_id, ps.fee_status, ps.price_paid, i.upload_file_name from places p join place_rate pr on p.id= pr.place_id join place_service ps on p.id = ps.place_id and ps.fee_status != 'N/A' left join place_image i on p.id = i.place_id and i.role = 'thumbnail'
and p.area_code = #{areaCode}
and p.name like concat('%', #{name}, '%')
and pr.rate_id = #{rateId}
and p.id in (SELECT p.id FROM places p JOIN place_service ps ON p.id = ps.place_id WHERE ps.service_id in
#{id}
and ps.fee_status != 'N/A' GROUP BY p.id HAVING COUNT(DISTINCT ps.service_id) = #{serviceIdtotalCount})
order by pr.price asc

select p.id, p.name, p.address, p.phone, pr.rate_id, pr.price, s.service_id, s.fee_status, s.price_paid from places p left join place_rate pr on p.id = pr.place_id left join place_service s on p.id = s.place_id left join place_image i on p.id = i.place_id and i.role='thumbnail'
and p.area_code = #{areaCode}
and p.name like concat('%', #{name}, '%')

mapper.update:
update place_rate
set
price = #{price}
where
rate_id = #{rateId} and
place_id = #{placeId}
